    private void insertarBotActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_insertarBotActionPerformed
        String competencia = competenciaText.getText();
        
        if(competencia.length() == 0){
            
           this.dispose();
           ListadoPorCompetencias buscarP = new ListadoPorCompetencias("","");
           buscarP.setVisible(true);
           return;
        }
        
        else if(PrincipalAtletismo.claseMain.getCompetencia(competencia) == null){
            
            JOptionPane.showMessageDialog(this, "Esta competencia no ha sido registrada");
            return;
        }
        
        this.dispose();
        ListadoBuscarCompePrueba buscarP = new ListadoBuscarCompePrueba(competencia);
        buscarP.setVisible(true);
    }//GEN-LAST:event_insertarBotActionPerformed

    private void regresarBotActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_regresarBotActionPerformed
        this.dispose();
        ListadoMarcasPantalla Listado = new ListadoMarcasPantalla();
        Listado.setVisible(true);
    }//GEN-LAST:event_regresarBotActionPerformed

