    private void regresarBotActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_regresarBotActionPerformed
        this.dispose();
        MarcaPantalla MarcaP = new MarcaPantalla();
        MarcaP.setVisible(true);
    }//GEN-LAST:event_regresarBotActionPerformed

    private void insertarBot2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_insertarBot2ActionPerformed
        String competencia = nomCompeText2.getText();
        
        if(PrincipalAtletismo.claseMain.getCompetencia(competencia) == null){
            
            JOptionPane.showMessageDialog(this, "Esta competencia no ha sido registrada");
            return;
        }
        
        String prueba = nomPruebaText1.getText();
        
        if(PrincipalAtletismo.claseMain.validarCompetenciasPruebas(competencia, prueba) == null){
            
            JOptionPane.showMessageDialog(this, "Esta prueba no está registrada en esta competencia");
            return;
        }
        
        String idAtleta = idAtletaText.getText();
        
        if(PrincipalAtletismo.claseMain.validarCompetenciaPruebaAtleta(competencia, prueba, idAtleta) == null){
            
            JOptionPane.showMessageDialog(this, "Esta atleta no está registrado en esta prueba en esta competencia");
            return;
        }
        
        String marca = marcaText.getText();
        
        PrincipalAtletismo.claseMain.setRegistroMarcas(competencia, prueba, idAtleta, marca);
        
        this.dispose();
        MarcaPantalla MarcaP = new MarcaPantalla();
        MarcaP.setVisible(true);
    }//GEN-LAST:event_insertarBot2ActionPerformed

